Scripts.OnClick:
		                            CT_RaidTrackerLoadDefaultOptions();
    								CT_RaidTracker_Print("Standalone Raid Tracking Options Loaded", 1, 1, 0);
    								self:GetParent():GetParent():Hide();


CT_RaidTrackerLoadDefaultEPGPOptions();
CT_RaidTracker_Print("Standalone Raid Tracking w/ EPGP Options Loaded", 1, 1, 0);
self:GetParent():GetParent():Hide();

CT_RaidTrackerLoadDefaultDKPmonOptions();
CT_RaidTracker_Print("DKPmon Integration Options Loaded", 1, 1, 0);
CT_RaidTracker_Print("Ensure that DKPmon is loaded and set to log to Raid Tracker", 1, 1, 0);
self:GetParent():GetParent():Hide();

Scripts.OnUpdate:
local colors = {
"|c00ffffffOff",
"|c00ffffffOn mouseover",
"|c00ffffffOn boss kill",
};
getglobal(self:GetName() .. "Value"):SetText(colors[self:GetValue()+1]);

local text = {
"|c00ffffffOff",
"|c00ffffffOn boss kill",
"|c00ffffffOn loot",
"|c00ffffffOn boss kill (same Zone)",
};
getglobal(self:GetName() .. "Value"):SetText(text[self:GetValue()+1]);

local colors = {
"|c00ffffffMLdkp 1.1/EQdkp Plugin",
"|c00ffffffEQdkp Unknown",
"|c00ffffffMLdkp 1.5",
"|c00ffffffGuild Launch",
};
getglobal(self:GetName() .. "Value"):SetText(colors[self:GetValue()+1]);
