Scripts.OnUpdate:
local colors = {
"|c00ffffffOff",
"|c00ffffffOn mouseover",
"|c00ffffffOn boss kill",
};
getglobal(this:GetName() .. "Value"):SetText(colors[this:GetValue()+1]);

local text = {
"|c00ffffffOff",
"|c00ffffffOn boss kill",
"|c00ffffffOn loot",
"|c00ffffffOn boss kill (same Zone)",
};
getglobal(this:GetName() .. "Value"):SetText(text[this:GetValue()+1]);

local colors = {
"|c00ffffffMLdkp 1.1/EQdkp Plugin",
"|c00ffffffEQdkp Unknown",
"|c00ffffffMLdkp 1.5",
"|c00ffffffGuild Launch",
};
getglobal(this:GetName() .. "Value"):SetText(colors[this:GetValue()+1]);
